//
function inlineAffixAnalysis(keyword, check) {
  $('#external_search_div').hide();
  if($('#affixanalysisresults').is(':visible')){
    // Toggle off:
    $('#affixanalysisresults').fadeOut(150);
  }
  else {
    $('#affixanalysisresults').html('<span style="color:#888; font-weight:Bold; font-size:16px;">Analyzing "'+keyword+' for Tagalog affix patterns...</span><br><br><br><br><br><br><br><br>').fadeIn(50);

    // Toggle on:
    $.ajax({
      url: '/affix-analysis/ajax/analyze.php',
      data: {
        keyword: keyword,
        check: check
      },
      error: function () {
      },
      success: function (data) {
        $('#external_search_div').hide();

        var html = '';

        // Close button:
        html += '<a href="#" onclick=" $(\'#affixanalysisresults\').fadeOut(150); return false;" class="close-link" style="float:right;">';
        html += '<img src="https://www.tagalog.com/images/slowcache/close.svg" class="no-user-select" width="32" height="32">';
        html += '</a>';

        html += '<div style="font-size:18px; font-weight:Bold; margin-bottom:18px;">Affix Analysis:</div>';

        // Main results:
        html += data;

        $('#affixanalysisresults').html(html).fadeIn(150);
      },
      type: 'GET'
    });
  }
}

