function clearReferenceSearch() {
  $refkeyword = $('#search_keyword');
  $refkeyword.val('');
  $refkeyword.focus();
  $('#reference_search_clear').hide();
  $('#reference_search_clear_disabled').show();
  updateReferenceSearchResults('search_keyword', 'refresults');
  ref_selection_count = 0;
}

///
function showReferenceClearBtn() {

  var $refkeyword = $('#search_keyword');
  var $reference_search_clear = $('#reference_search_clear');
  if($refkeyword.val()) {
    $('#reference_search_clear_disabled').hide();
    $reference_search_clear.fadeIn(400).css('display', 'inline-block'); // you could still use `.hide()` here
  }
  else {
    clearReferenceSearch();
  }

  $refkeyword = null;
  $reference_search_clear = null;
}

