function getRefSelectedText() {
  var selectedRange;
  try {
    if (window.getSelection) {
      selectedRange = window.getSelection().toString();
    }
    else {
      selectedRange = document.getSelection().toString();
    }
  }
  catch (err) { }
  return selectedRange;
}

//
function postSelectedTextToReferenceSearch() {

  var selectedtxt = '';

  selectedtxt = getRefSelectedText();

  if(selectedtxt) {
    selectedtxt = selectedtxt.replace(/<br>/g, " "); // remove newlines
    selectedtxt = stripHTML(selectedtxt);
    selectedtxt = selectedtxt.replace(/\n/g, " "); // remove newlines
    selectedtxt = selectedtxt.trim();
    if(selectedtxt) {
      $('#search_keyword').val(selectedtxt);
      showReferenceClearBtn();
    }
  }
  else {
    // No text found that was selected...
  }
}

//
function postTextToReferenceSearch(txt, hideform, dont_reload_overlay, nofocus) {

  if(txt) {
    console.log('Keyword2: '+txt);
    if(!dont_reload_overlay) {
      showReferenceOverlay(hideform, nofocus);
    }
    txt = txt.replace(/<br>/g, " "); // remove newlines
    txt = stripHTML(txt);
    txt = removeNbsp(txt);
    txt = txt.replace(/\n/g, " "); // remove newlines
    txt = txt.trim();
    if(txt) {
      console.log('Keyword3: '+txt);
      $('#search_keyword').val(txt);
      updateReferenceSearchResults('search_keyword', 'refresults');
      showReferenceClearBtn();
    }
  }
  else {
    // No text found that was selected...
  }
}


//
function handleRefKeys(keyid) {

  if(keyid === 13) {
    $('#search_keyword').blur();
    console.log('Blur after enter x2');
  }
  else if(keyid === 40) {
    // DOWN arrow:
    if(ref_selection_count >= 10) {
      ref_selection_count = 10;
    }
    else {
      ref_selection_count++;
    }
  }
  else if(keyid === 38) {
    // UP arrow:
    if(!ref_selection_count || ref_selection_count <= 1) {
      ref_selection_count = 0;
    }
    else {
      ref_selection_count--;
    }
  }

  for(var x=1; x<=10; x++) {
    selection_id = 'search_result'+x;
    if(x === ref_selection_count) {
      $('#'+selection_id).addClass('selected-ref');
    }
    else {
      $('#'+selection_id).removeClass('selected-ref');
    }

  }

}

