function updateReferenceSearchResults(inputid, refresultsid, no_log, no_clip, instant) {

  console.log('updateReferenceSearchResults');

  var throttleSpeed = 250;
  if(instant) {
    throttleSpeed = 0;
  }

  var keyword = $('#'+inputid).val();
  var $results = $('#'+refresultsid);

  if(last_search_keyword === keyword) {
    return;
  }
  last_search_keyword = keyword;

  keyword = keyword.toLowerCase();
  clearTimeout(waiting_timeout);
  clearTimeout(refsearchtimer);
  clearTimeout(ref_timeout);

  if(reference_search_xhr) {
    // Cancel any prior requests to fetch AJAX results...
    reference_search_xhr.abort();
    reference_search_xhr.onreadystatechange = null;
    reference_search_xhr.abort = null;
    reference_search_xhr = null;
  }

  if(keyword.length === 1 && keyword !== 'o' && keyword !== 'O') {
    // For efficiency and resources reasons...
    // We only allow ONE one-letter keyword, "o" meaning "or" or "oh"
    current_ref_cache = '';
    document.getElementById(refresultsid).innerHTML = ""; // kill memory leak problem...
    $results.empty().html('');
    clearTimeout(refsearchtimer);
  }
  else if(keyword.length === 2 && !allowed_two_letter_searches[keyword]) {
    // For efficiency and resources reasons...
    // For two letter searches, only allow searches on our whitelist of valid two-letter searches
    current_ref_cache = '';
    document.getElementById(refresultsid).innerHTML = ""; // kill memory leak problem...
    $results.empty().html('');
    clearTimeout(waiting_timeout);
    clearTimeout(refsearchtimer);
    clearTimeout(ref_timeout);

  }
  else if(keyword && keyword.length >= 1 && !exclude_from_reference_search[keyword]) {
    // set the search on a timer, so that in the case of a fast typer, you're not doing 8 queries in a second...

    if(false && ref_results[keyword] && current_ref_cache !== keyword) {
      // if we've already cached the results, don't fetch them again...saves a LOT
      // of db queries (esp in typos or slight keyword modifications)
      // ALSO makes the server / site appear to be lightning fast
      document.getElementById(refresultsid).innerHTML = ""; // kill memory leak problem...
      $results.empty().html(ref_results[keyword]);
      $results.fadeIn(75);
      current_ref_cache = keyword; // important: don't load from cache if already loaded...creates a problem with clicking (having to click twice b/c it reloads on onKeyUp and onChange)
    }
    else {
      clearTimeout(waiting_timeout);
      clearTimeout(refsearchtimer);
      clearTimeout(ref_timeout);

      current_ref_cache = keyword;

      console.log('Instant: '+instant);

      if(!instant) {
        console.log('Initiating throttle');
        refsearchtimer = setTimeout(function () {
          getReferenceSearchResults(keyword, refresultsid, inputid, no_log, no_clip, instant);
        }, throttleSpeed);
      }
      else {
        //
        // Instant search:
        // -- Don't use the timer, just fetch results immediately
        // -- Don't create a timeout ID, because I never want this overridden!
        console.log('No throttle');
        getReferenceSearchResults(keyword, refresultsid, inputid, no_log, no_clip, true);
      }
    }
  }
  else {
    current_ref_cache = '';
    if(document.getElementById(refresultsid).innerHTML) {
      document.getElementById(refresultsid).innerHTML = ""; // kill memory leak problem...
      $results.empty().html('');
    }
    clearTimeout(refsearchtimer);
  }

  $results = null;
}

