function doDidYouMeanCheck(keyword, refresultsid, inputid) {
  if(keyword) {
    $.ajax({
      url: '/translate/did_you_mean.php',
      data: {
        content: keyword,
        key: server_key
      },
      error: function () {
      },
      success: function (json) {
        if(json) {
          let data = JSON.parse(json);
          if(data.is_real_tagalog_word === 0 && data.closest_tagalog_word && data.closest_tagalog_word !== '') {
            let html = '';
            let safe_keyword = data.closest_tagalog_word;
            // Remove single and double quotes:
            safe_keyword = safe_keyword.replace(/'/g, '');
            safe_keyword = safe_keyword.replace(/"/g, '');
            safe_keyword = safe_keyword.replace(/`/g, '');
            // Remove fancy quotes:
            safe_keyword = safe_keyword.replace(/‘/g, '');
            safe_keyword = safe_keyword.replace(/’/g, '');
            safe_keyword = safe_keyword.replace(/“/g, '');
            safe_keyword = safe_keyword.replace(/”/g, '');
            html += '<div style="background-color:#dfe; padding:8px; text-align:center; margin-top:12px; margin-bottom:12px; font-size:16px; color:#666;">';
            html += 'Did you mean: ';
            html += '<a href="#" onClick="$(\'#'+inputid+'\').val(\''+safe_keyword+'\'); updateReferenceSearchResults(\''+inputid+'\', \''+refresultsid+'\', 1, 0); return false;" style="color:#1f4779; font-style:italic; font-weight:bold; background-color:rgba(255,255,255,0.3); display:inline-block; padding:2px 4px; text-decoration:underline;">';
            html += data.closest_tagalog_word;
            if(data.closest_word_english_translation && data.closest_word_english_translation !== ''
              && data.closest_word_english_translation !== data.closest_tagalog_word) {
              html += ' = '+data.closest_word_english_translation;
            }
            html += '</a>?';
            html += '</div>';
            $('#didyoumean').html(html).fadeIn(150);
          }
        }
      },
      type: 'GET'
    });
  }
}

